import React from "react";
import logo from "../../../Assets/Navbar/logo.png";
import avatar from "../../../Assets/Navbar/UserAvatar-signed.png";
import vector2 from "../../../Assets/Navbar/Vector-2.png";
import vector3 from "../../../Assets/Navbar/Vector-3.png";
import DarkMode from "../../../Components/Switch/SwitchMode";
import { NavLink } from "react-router-dom";
import "./Dash-NavBar.css";

const DashNavBar = () => {
  const activeUser = useSelector((state) => state.activeUser);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();


  if (activeUser) {
    window.localStorage.setItem("user", JSON.stringify(activeUser.userToken));
    window.localStorage.setItem("token", JSON.stringify(activeUser.token));
  }

  useEffect(() => {
    setUser(window.localStorage.getItem("user"));
  }, []);

  let data = user ? JSON.parse(user) : null;
  let theme = localStorage.getItem("theme");
  let imageUrl = data ? data.image : null;
  let imgProfileSrc = imageUrl ? imageUrl : vector;
  let classProfileImage = imageUrl ? "googleImg" : "profile";

  const logOut = async (e) => {
    e.preventDefault();
    await swal({
      title: "You are about to logout",
      text: "Are you sure wanna go out?",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        swal("See you around!", {
          icon: "success",
        }).then(() => {
          dispatch(logoutUser());
          navigate("/home");
          window.history.go();
          localStorage.setItem("theme", theme);
        });
      }
    });
  };





  return (
    <>
      <div className="DashcontainerNavbar">
        <NavLink to={types.length? "/home?type=" + types[0]: "/home"} className="Dashlink-navbar">
          <img src={logo} alt="logo" className="Dashlogo" />
        </NavLink>

        <div className="DashiconsContainer">
          <div className="Dashitem">
          <NavLink
            to={data !== null ? "/users" : "/login"}
            className="link-navbar"
          >
            <span>Users</span>
          </NavLink>
            <NavLink to={data !== null ? "/create" : "/login"} className="Dashlink-navbar">
              <img src={vector3} alt="vector3" className="Dashicons" />
              <span>New Pet</span>
            </NavLink>
          </div>

          <div className="Dashitem">
            <NavLink to={data !== null ? "/favorites" : "/login"} className="Dashlink-navbar">
              <img src={vector2} alt="vector2" className="Dashicons" />
              <span>My favorites</span>
            </NavLink>
          </div>

          <div className="Dashitem">
            <DarkMode />
          </div>

          {data ? (
          <a href="/home" onClick={(e) => logOut(e)} className="Dashlink-navbar">
            LOG OUT
          </a>
        ) : (
          <NavLink to={"/login"} className="Dashlink-navbar">
            <span>LOG IN</span>
          </NavLink>
        )}
        <NavLink
          to={data !== null ? "/profile" : "/login"}
          className={classProfileImage}
        >
          <div className={classProfileImage}>
            <img src={imgProfileSrc} alt="vector" id="imgProfile" />
          </div>
        </NavLink>

         

        </div>
      </div>
    </>
  );
};

export default DashNavBar;
