import React, { useEffect } from "react";
import "./User.css"
import DashNavBar from "../Dash-NavBar/Dash-NavBar";
// import NavBar from "../../../Components/Navbar/Navbar.jsx";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, getUsers } from "../../../Redux/Actions/index.js";
import swal from "sweetalert";
import { useParams } from "react-router-dom";

export default function DashUsers() {
    const {mail} = useParams();
    console.log('mail :>> ', mail);
  const dispatch = useDispatch()
  const users = useSelector(s => s.user)
  console.log('users :>> ', users);   

 useEffect(() => {
    dispatch(getUsers())

 },[dispatch])

 function handleDeleteUser(){
    dispatch(deleteUser("lucasdiaz687@gmail.co"))
}



 
  return (
    <div>

        <DashNavBar/>
      <div className="mainDashContU">
        {/* <NavBar/> */}
    {/* {isLoading || favorites.length === 0 ? null : ( */}
      <>
        <div className="conTituloU infoU">
          <h3>USERS</h3>
        </div>
        <div className="infoUser">
       { users && users?.map((u)=> { 
           return ( 
               <div key={Math.random()} className="favContainerU">
                <div className="favcardLeftPhotoU">
                    <div className="imgFavorU">
                      {u.image && (
                          <img src={u.image} alt="user" className="img" />
                      )}
                    </div>
                  <div>
                    <button
                    onClick={() => handleDeleteUser(mail)}
                      className="a-btnFavElimU"
                    />
                  </div>
                </div>
                <div className="favcardLeftU">
                    
                  <span>{u.name}</span>
                  <span>{u.lastName}</span>
                  
                </div>
                <div className="favcardCenterU">
                    <span>{u.userName}</span>
                    <span>{u.mail}</span>
                </div>
                {/* <div className="favcardRight">
                   <span>{description}</span> 
                </div> */}
              </div>
          )})}
        </div>
      </>
    {/* )} */}
  </div>
      
          </div>
  );
}
