import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {deleteUser, getUsers} from "../../Redux/Actions/index.js"
import { useEffect, useState } from "react";
import "./UserDetail.css"



function UserDetail({name, mail, userName, image, lastName}){
  const dispatch = useDispatch()
 
  let user = null;
  if(localStorage.user){
    const userJson = localStorage.getItem("user");
    user = JSON.parse(userJson);
  }
  
  if(user){
    var mail = user.mail? user.mail : user.email
  }
  useEffect(() => {
   dispatch(getUsers())
  },[] );

  

  function handleDeleteUser(){
    dispatch(deleteUser(mail))
}

  return (
    <div className="infoUser">
               <div key={Math.random()} className="favContainerU">
                <div>
                    <button
                    onClick={() => handleDeleteUser(mail)}
                      className="a-btnFavElimU"
                    />
                  </div>
                  
                <div className="favcardLeftPhotoU">
                    <div className="imgFavorU">
                      {image && (
                          <img src={image} alt="user" className="img" />
                          )}
                    </div>
                  
                </div>
                <div className="favcardLeftU">
                    
                  <span>{name}</span>
                  <span>{lastName}</span>
                  
                </div>
                <div className="favcardCenterU">
                    <span>{userName}</span>
                    <span>{mail}</span>
                </div>
              </div>
        </div>
  );
}

export default UserDetail;
