import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {getUsers} from "../../Redux/Actions/index.js"
import { useEffect, useState } from "react";
import "./UserDetail.css"



function UserDetail({name, mail, userName, image, lastName}){
  const dispatch = useDispatch()
  const users = useSelector((state)=> state.user)
 
  let user = null;
  if(localStorage.user){
    const userJson = localStorage.getItem("user");
    user = JSON.parse(userJson);
  }
  
  if(user){
    var mail = user.mail? user.mail : user.email
  }
  useEffect(() => {
   dispatch(getUsers())
  },[] );

  


  return (
    <div className="infoUser">
       { users && users?.map((u)=> { 
           return ( 
               <div key={Math.random()} className="favContainerU">
                <div>
                    <button
                    onClick={() => handleDeleteUser(u.mail)}
                      className="a-btnFavElimU"
                    />
                  </div>
                  
                <div className="favcardLeftPhotoU">
                    <div className="imgFavorU">
                      {u.image && (
                          <img src={u.image} alt="user" className="img" />
                          )}
                    </div>
                  
                </div>
                <div className="favcardLeftU">
                    
                  <span>{u.name}</span>
                  <span>{u.lastName}</span>
                  
                </div>
                <div className="favcardCenterU">
                    <span>{u.userName}</span>
                    <span>{u.mail}</span>
                </div>
              </div>
          )})}
        </div>
  );
}

export default UserDetail;
