import { Link, useLocation, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {deleteUser, getUsers} from "../../Redux/Actions/index.js"
import { useEffect, useState } from "react";
import "./UserDetail.css"



function UserDetail({name, mail, userName, image, lastName}){
  const dispatch = useDispatch()
    const navigate = useNavigate()
    const {pathname, search} = useLocation()
    console.log('pathname', pathname)
    console.log('search', search)
  function handleDeleteUser(e){
    e.preventDefault()
    dispatch(deleteUser(mail))
   navigate(pathname)
}

  return (
    <div className="infoUser">
               <div key={Math.random()} className="favContainerU">
                <div>
                    <button onClick={(e) => handleDeleteUser(e)}>x</button>
                  </div>
                  
                <div className="favcardLeftPhotoU">
                    <div className="imgFavorU">
                      {image && (
                          <img src={image} alt="user" className="img" />
                          )}
                    </div>
                  
                </div>
                <div className="favcardLeftU">
                    
                  <span>{name}</span>
                  <span>{lastName}</span>
                  
                </div>
                <div className="favcardCenterU">
                    <span>{userName}</span>
                    <span>{mail}</span>
                </div>
              </div>
        </div>
  );
}

export default UserDetail;
