import { useDispatch, useSelector } from "react-redux";
import {addFavs, deleteFavs} from "../../Redux/Actions/index.js"
import { useEffect, useState } from "react";



function UserDetail({name, mail, userName, image, lastName}){
  const dispatch = useDispatch()
 
  let user = null;
  if(localStorage.user){
    const userJson = localStorage.getItem("user");
    user = JSON.parse(userJson);
  }
  
  if(user){
    var mail = user.mail? user.mail : user.email
  }
  useEffect(() => {
   
  },[] );

  


  return (
    <div className="lcard">
      <img src={image} alt="foto" />
      <div className="txtCont">
        <div className="txtLeft">
          <div className="title">{name}</div>
          <div className="breed">{breed}</div>
          <div className="age">{age} years</div>
          <div className="location">{location}</div>
        </div>
        <div className="txtRight">
          <div className="views">
            <span className="icoEye"></span>
            Views
          </div>
          <div className="size">
            <span className="icoSize"></span>
            {size}
          </div>
          <div className="weigth">
            <span className="icoWeigth"></span>
            3.4 kg
          </div>
        </div>
        <div className="attributes">{description}</div>
      </div>
      {/* <Link to={`/home/${id}`} className="lbutton">
        <div>
          <span>More info</span>
        </div>
      </Link> */}
    </div>
  );
}

export default UserDetail;
