import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getUserId, getUsersBanned } from "../../Redux/Actions";
import UserDetail from "../UserDetail/UserDetail";


export default function UsersBanned() {
    const {mail} = useParams()
  const dispatch = useDispatch()
  const users = useSelector(s => s.userBanned)
  console.log('mail', mail)


    useEffect(() => {
        dispatch(getUsersBanned())
    }, [])

    function handleRestoredUser(e){
        e.preventDefault()
        dispatch(deleteUser(e.target.id))
            window.history.go()
        }
    
    
    
  return (
    <div>
        { users && users.map((u) =>
