import React, { useEffect } from "react";
import DashNavBar from "../Dash-NavBar/Dash-NavBar";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, getUsers, makeAdmin } from "../../../Redux/Actions/index.js";
import UserDetail from "../../../Components/UserDetail/UserDetail";
import SearchUser from "../../../Components/SearchUser/SearchUser";
import Navbar from "../../../Components/Navbar/Navbar";

export default function DashUsers() {
    
  const dispatch = useDispatch()
  const allUsers = useSelector(s => s.user)
  const users = allUsers.filter(e => e.isAdmin === false)

    useEffect(() => {
        dispatch(getUsers())
    },[dispatch])

    function handleDeleteUser(e){
    e.preventDefault()
    dispatch(deleteUser(e.target.id))
        window.history.go()
    }
    function handleAdmin(e){
    e.preventDefault()
    dispatch(makeAdmin(e.target.id))
        window.history.go()
    }

  return (
    <div>
      <DashNavBar/>
      {/* <Navbar/> */}
      <div>
         <>
            <div>
                <h3>USERS</h3>
                <SearchUser/>
            </div>
            <div>
                { users && users.map((u)=>
                    <UserDetail

                        handleAdmin={handleAdmin}
                        handleDeleteUser={handleDeleteUser}
                        name={u.name}
                        lastName={u.lastName}
                        mail={u.mail}
                        image={u.image}
                        userName={u.userName}
                        isAdmin={u.isAdmin}
                    />
                )}
            </div>
            </>
        </div>
    </div>
  );
}
